package ohyes.coffee.dto;

public class ReviewCommentDTO {
	private int reviewCoNo;
	private int reviewNo;
	private String reviewCoDate;
	private String reviewCoContent;
	private String reviewCoId;
	
	public ReviewCommentDTO() {
		// TODO Auto-generated constructor stub
	}

	public int getReviewCoNo() {
		return reviewCoNo;
	}

	public void setReviewCoNo(int reviewCoNo) {
		this.reviewCoNo = reviewCoNo;
	}

	public int getReviewNo() {
		return reviewNo;
	}

	public void setReviewNo(int reviewNo) {
		this.reviewNo = reviewNo;
	}

	public String getReviewCoDate() {
		return reviewCoDate;
	}

	public void setReviewCoDate(String reviewCoDate) {
		this.reviewCoDate = reviewCoDate;
	}

	public String getReviewCoContent() {
		return reviewCoContent;
	}

	public void setReviewCoContent(String reviewCoContent) {
		this.reviewCoContent = reviewCoContent;
	}

	public String getReviewCoId() {
		return reviewCoId;
	}

	public void setReviewCoId(String reviewCoId) {
		this.reviewCoId = reviewCoId;
	}

	
}
